    @Test
    public void testSerialization1() throws Exception {
      //  Nd4j.dtype = DataBuffer.Type.DOUBLE;

        INDArray test = Nd4j.ones(1,10);

        INDArray[] arr = new INDArray[]{
                Nd4j.ones(1,10),
//                Nd4j.ones(5,10).getRow(2)
        };

        System.out.println("X1 ---------------------------");

        for(INDArray a : arr) {
            ByteArrayOutputStream baos = new ByteArrayOutputStream();
            try(ObjectOutputStream oos = new ObjectOutputStream(baos)){
                oos.writeObject(a);
                oos.flush();
            }

            System.out.println("X2 ---------------------------");

            byte[] bytes = baos.toByteArray();

            ByteArrayInputStream bais = new ByteArrayInputStream(bytes);
            ObjectInputStream ois = new ObjectInputStream(bais);




            System.out.println("X3 ---------------------------");

            INDArray aDeserialized = (INDArray) ois.readObject();

            System.out.println("X4 ---------------------------");

            assertEquals(test, aDeserialized);

            System.out.println("Deserialized: " + aDeserialized);
        }
    }

    @Test
    public void testDeserialization2() throws Exception {
        Nd4j.getRandom().setSeed(12345);
        INDArray arr = Nd4j.rand(1,20);

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        try(ObjectOutputStream oos = new ObjectOutputStream(baos)){
            oos.writeObject(arr);
            oos.flush();
        }

        byte[] bytes = baos.toByteArray();

        ByteArrayInputStream bais = new ByteArrayInputStream(bytes);
        ObjectInputStream ois = new ObjectInputStream(bais);



        System.out.println("X3 ---------------------------");

        INDArray aDeserialized = (INDArray) ois.readObject();
        assertEquals(arr, aDeserialized);

        assertEquals(arr, aDeserialized.dup());
    }

